def read_float_list_from_csv(filename):
    float_list = []

    try:
        with open(filename, 'r') as file:
            reader = csv.reader(file, delimiter='\t')
            for line in reader:
                for value in line:
                    try:
                        float_value = float(value)
                        float_list.append(float_value)
                    except ValueError:
                        print(f"")
    except IOError:
        print(f"Unable to open file: {filename}")

    return float_list


